import {
  Box, Button, Card, CardActions, CardActionArea, CardContent, CircularProgress, Typography,
} from '@material-ui/core';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

import { useApi } from '../util';

const Item = () => {
  const api = useApi();
  const { movieId } = useParams();
  const [movie, setMovie] = useState();
  useEffect(() => api.getMovie(+movieId).then(([d]) => setMovie(d)), [api, movieId]);

  if (!movie) {
    return <CircularProgress />;
  }
  console.log(movie);

  return (
    <Card>
      <CardActionArea>
        <img alt={movie.title} src={movie.large_cover_image} />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {movie.title_long}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {movie.description_full}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Share
        </Button>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card>
  );
};

export default Item;
