import { Box, CircularProgress, MenuItem, TextField } from '@material-ui/core';
import React, { useEffect, useState } from 'react';

import { useApi } from '../util';
import { MovieCard } from '../components';
import { useHistory } from 'react-router-dom';

const Main = () => {
  const api = useApi();
  const [movies, setMovies] = useState();
  const history = useHistory();

  useEffect(() => { api.getMovies().then(setMovies) }, [api]);

  if (!movies) {
    return <CircularProgress />;
  }

  let genresArray = [];
  movies.forEach(({ genres }) => {
    genresArray = [...genresArray, ...genres || []];
  });
  const genres = [...new Set(genresArray)];

  return (
    <Box>
      <TextField label="Genres" select>
        {genres.map((genre) => <MenuItem key={genre}>{genre}</MenuItem>)}
      </TextField>
      {movies.map(({description_full, id, medium_cover_image, title }) => (
      <MovieCard
        description={description_full}
        img={medium_cover_image}
        key={id}
        onClick={() => history.push(`${id}`)}
        title={title}
      />))}
    </Box>
  );
}

export default Main;
